mapper.update:
UPDATE voucher
SET date = #{date}, total = #{total},
    tx_id = #{txId}, tx_host= #{txHost}, tx_user_id= #{txUserId}, tx_date= #{txDate}
WHERE voucher_id = #{voucherId}
AND tx_status=1

UPDATE  voucher
SET tx_status = #{txStatus},
    tx_id = #{txId}, tx_host= #{txHost}, tx_user_id= #{txUserId}, tx_date= #{txDate}
WHERE voucher_id = #{voucherId}
